import React from "react";
import { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import IndexPage from "./IndexPage";
import Signup from './Signup'
import Wastedisplay from './Wastedisplay'
//import Login from './Login'
import Uploadwaste from "./Uploadwaste";

function App() {
  const [refresh, setRefresh] = useState(false);

  const handleUploadSuccess = () => {
    setRefresh(!refresh); // Trigger a re-fetch in WasteDataDisplay
  };
  return (
    <Router>
      <Routes>
        <Route path="/" element={<IndexPage />} />
        <Route path="/Signup" element={<Signup />} />
        <Route path="/Login" element={<WasteDataDisplay key={refresh} />} />
        <Route path="/uploadwaste" element={<WasteUploadForm onUploadSuccess={handleUploadSuccess} />} />
      </Routes>
    </Router>
  );
}

export default App;
