import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [admin, setAdmin] = useState(null);

  useEffect(() => {
    // Fetch admin details from local storage
    const storedAdmin = JSON.parse(localStorage.getItem("admin"));
    if (!storedAdmin) {
      navigate("/login"); // Redirect to login if not authenticated
    } else {
      setAdmin(storedAdmin);
    }
  }, [navigate]);

  const handleViewReports = () => {
    navigate("/reports");
  };
  const handleLogout = () => {
    localStorage.removeItem("admin"); // Clear admin details
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="body">
        <nav style={{width:"100%"},{height:"40px"}}>

        </nav>
    <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", textAlign: "center", border: "1px solid #ddd", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0,0,0,0.1)" }}>
      <h2>Admin Dashboard</h2>

      {admin ? (
        <div>
          <h3>Welcome, {admin.name} ðŸ‘‹</h3>
          <p>Email: {admin.email}</p>

          <div style={{ marginTop: "20px", padding: "10px", borderRadius: "5px", background: "#f8f9fa" }}>
            <h4>About Waste Management</h4>
            <p>
              Waste management is essential for maintaining a clean and sustainable environment. 
              Proper disposal, recycling, and waste reduction play a crucial role in reducing pollution and protecting natural resources.
            </p>
          </div>

          <button 
            onClick={handleViewReports} 
            style={{ marginTop: "20px", padding: "10px 20px", background: "#007bff", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
          >
            View Reports
          </button>
          <button onClick={handleLogout} style={{ background: "red", color: "white", padding: "10px", borderRadius: "5px" }}>
            Logout
         </button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
    </div>
  );
}
