import { useEffect, useState } from "react";

export default function Wastedisplay() {
  const [wasteReports, setWasteReports] = useState([]);

  useEffect(() => {
    fetchWasteReports();
  }, []);

  const fetchWasteReports = async () => {
    try {
      const response = await fetch("http://localhost:5000/reports");
      const data = await response.json();
      setWasteReports(data);
    } catch (error) {
      console.error("Error fetching waste reports:", error);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2 style={{ fontSize: "22px", fontWeight: "bold", marginBottom: "15px" }}>Waste Reports</h2>
      {wasteReports.length === 0 ? (
        <p>No waste reports available.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {wasteReports.map((report) => (
            <li key={report.id} style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "5px", marginBottom: "10px", boxShadow: "2px 2px 8px rgba(0,0,0,0.1)" }}>
              {report.image_path && <img src={`http://localhost:5000/uploads/${report.image_path}`} alt="Waste" style={{ maxWidth: "100%", height: "auto", marginBottom: "10px" }} />}
              <p><strong>Address:</strong> {report.address}</p>
              <p><strong>Quantity:</strong> {report.quantity} kg</p>
              <p><strong>Location:</strong> {report.latitude}, {report.longitude}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
