import { useState, useEffect } from "react";

export default function Wastedisplay() {
  const [wasteReports, setWasteReports] = useState([]);

  // Fetch waste reports from the backend
  const fetchWasteReports = async () => {
    try {
      const response = await fetch("http://localhost:5000/reports"); // API URL
      const result = await response.json();
      setWasteReports(result);
    } catch (error) {
      console.error("Error fetching waste reports:", error);
    }
  };

  // Fetch data when the component loads
  useEffect(() => {
    fetchWasteReports();
  }, []);

  if (wasteReports.length === 0) {
    return <p style={{ textAlign: "center", fontSize: "18px", color: "#777" }}>No waste reports available</p>;
  }

  return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "auto" }} >
      <h2 style={{ fontSize: "22px", fontWeight: "bold", marginBottom: "15px", textAlign: "center" }}>Waste Reports</h2>
      {wasteReports.map((item, index) => (
        <div key={index} style={{ border: "1px solid #ccc", borderRadius: "10px", padding: "15px", marginBottom: "15px", boxShadow: "2px 2px 12px rgba(0,0,0,0.1)", backgroundColor: "#f9f9f9" }}>
          <div style={{display:"flex"}}>
            <div>
          {item.image_path && (
            <img src={`http://localhost:5000/uploads/${item.image_path}`} alt="Waste" style={{ width: "50%", borderRadius: "5px", marginBottom: "10px" }} />
          )}
          </div>
          <div style={{}}>
          <p><strong>Address:</strong> {item.address}</p>
          <p><strong>Estimated Quantity:</strong> {item.quantity} kg</p>
          {item.latitude && item.longitude && (
            <p><strong>Location:</strong> {item.latitude}, {item.longitude}</p>
          )}
          <p style={{ fontSize: "12px", color: "#666" }}><strong>Reported On:</strong> {new Date(item.created_at).toLocaleString()}</p>
          </div>
          </div>
        </div>
      ))}
    </div>
  );
}
